\c products_db;

CREATE TABLE IF NOT EXISTS products (
    id SERIAL PRIMARY KEY,
    name VARCHAR(255),
    category VARCHAR(255),
    technical_details VARCHAR(1000),
    annual_value NUMERIC(10, 2),
    photos VARCHAR(1000)
);

CREATE TABLE IF NOT EXISTS categories (
    id SERIAL PRIMARY KEY,
    category VARCHAR(255),
    photo VARCHAR(1000)
);



